mapper.select:
SELECT B.BOARD_SEQ, B.TITLE, B.CONTENTS, B.REG_DATE
FROM T_BOARD B




	AND B.TITLE LIKE CONCAT('%', #{parameter.keyword} , '%')



	AND B.BOARD_TYPE IN (

			#{value}

	)


ORDER BY B.REG_DATE DESC
LIMIT #{pageRequest.limit} , #{pageRequest.offset}

mapper.insert:
INSERT INTO boot.t_board
( TITLE, CONTENTS, REG_DATE, BOARD_TYPE)
VALUES(
	#{title}, #{contents}, NOW(), #{ boardType }
)

mapper.update:
UPDATE T_BOARD
SET
	TITLE = #{title},
	CONTENTS = #{contents}
WHERE BOARD_SEQ = #{boardSeq}
